import "./style.css";
import React, { useState, useEffect } from "react";
import { storage, API_BASE_URL } from "../../config"
import { Container, Col, Row, Table, Button } from 'react-bootstrap';

function CartPage() {
    const [cart, setCart] = useState(null);

    const [totalCost, setTotalCost] = useState(0);

    const fetchCart = async () => {
        const url = API_BASE_URL + "card_item/account/" + storage.getItem("id");
        try {
            const response = await fetch(url, {
                method: "GET",
                headers: {
                    'Authorization': `Bearer ${storage.getItem('token')}`,
                    "Content-Type": "application/json",
                }
            });
            const responseData = await response.json();
            setCart(responseData);
        } catch (error) {
            console.error(error);
        }
    }

    const updateTotalCost = () =>{
        let total = 0;

        if(!!cart){
            
            for(let i of cart)
                total += i.item.price;
        }

        setTotalCost(total);
    }

    useEffect(() => {
        fetchCart();
        updateTotalCost();
        
    }, []);

    const onDelete = async (id) => {
        const url = API_BASE_URL + "card_item/" + id;
        try {
            const response = await fetch(url, {
                method: "DELETE",
                headers: {
                    'Authorization': `Bearer ${storage.getItem('token')}`,
                    "Content-Type": "application/json",
                }
            });

            const responseData = await response.json();

            if(!responseData.keyword){
                await fetchCart();
                updateTotalCost();
            }
            else if(responseData.keyword === "ID"){
                alert("Произошла непредвиденная ошибка");
                console.error(responseData.message);
            }
        } catch (error) {
            console.error(error);
        }
    }

    const onPlus = async (item) => {
        alert(item.id);

        const url = API_BASE_URL + "card_item/";
        try {
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Authorization': `Bearer ${storage.getItem('token')}`,
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    id: item.id,
                    count: item.count + 1
                })
            });

            const responseData = await response.json();

            if(!responseData.keyword){
                await fetchCart();
                updateTotalCost();
            }
            else if(responseData.keyword === "ID"){
                alert("Произошла непредвиденная ошибка");
                console.error(responseData.message);
            }
        } catch (error) {
            console.error(error);
        }
    }

    const onMinus = async (item) => {
        if(item.count === 1){
            await onDelete(item.id);
            return;
        }


        const url = API_BASE_URL + "card_item/";
        try {
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Authorization': `Bearer ${storage.getItem('token')}`,
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    id: item.id,
                    count: item.count - 1
                })
            });

            const responseData = await response.json();

            if(!responseData.keyword){
                await fetchCart();
                updateTotalCost();
            }
            else if(responseData.keyword === "ID"){
                alert("Произошла непредвиденная ошибка");
                console.error(responseData.message);
            }
        } catch (error) {
            console.error(error);
        }
    }

    const renderItem = (item, index) => {
        const value = (
            <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.item.name}</td>
                <td>{item.item.price.toString() + " руб."}</td>
                <td>
                    <Row>
                        <Col className="col-auto">
                            <Button className='btn-primary' onClick={() => {onMinus(item)}}>-</Button>
                        </Col>
                        <Col className="col-auto fs-4 fw-bold">
                            {item.count}
                        </Col>
                        <Col className="col-auto">
                            <Button className='btn-primary' onClick={() => {onPlus(item)}}>+</Button>
                        </Col>
                    </Row>
                </td>
                <td>
                    <Button className='btn-danger' onClick={() => {onDelete(item.id)}}>Удалить</Button>
                </td>
            </tr>
        );

        return value;
    }

    return (
        <Container className="mt-3">
            <Row className="justify-content-start">
                <Col className="col-auto">
                    <p className="fs-2 fw-bold">Корзина:</p>
                </Col>
            </Row>
            <Row>
                {!!cart && cart.length !== 0 ? (
                    <Table striped bordered hover className="mt-3">
                        <thead>
                            <tr>
                                <th>#</th>
                                <th>Название</th>
                                <th>Цена</th>
                                <th>Количество</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {cart.map((item, index) => renderItem(item, index))}
                        </tbody>
                    </Table>) :
                    (
                        <Row className="justify-content-center">
                            <Col className="col-auto">
                                <p className='fs-3 mt-3'>Список пуст</p>
                            </Col>
                        </Row>
                    )}
            </Row>
        </Container>
    );
}

export default CartPage;
