import "./style.css";
import React, {useState, useEffect} from "react";
import { Navbar, Nav, Col, Row } from 'react-bootstrap';
import AuthorizationModal from "../AuthorizationModal/AuthorizationModal";
import { storage } from "../../config";
import { useNavigate } from "react-router-dom";

function NavigationBar() {
    const [showAuthorizationModal, setShowAuthorizationModal] = useState(false);
    const [role, setRole] = useState(storage.getItem('role'));

    const onAuthorization = () => {
        setShowAuthorizationModal(true);
    };

    const navigate = useNavigate();

    const onExit = () => {
        storage.removeItem('token');
        storage.removeItem('id');
        storage.removeItem('username');
        storage.removeItem('role');
        setRole(null);
        navigate("/");
    };

    useEffect(() => {
        const handleStorageChange = () => {
            const userRole = storage.getItem('role');
            setRole(userRole);
        };
        window.addEventListener('storage', handleStorageChange);

        return () => {
            window.removeEventListener('storage', handleStorageChange);
        };
    }, []);

    const renderRoleRoutes = () => {
        if (role === "ADMIN")
            return (
                <>
                    <Nav.Link href="/administration">Администрирование</Nav.Link>
                    <Nav.Link href="/materials">Материалы</Nav.Link>
                    <Nav.Link href="/projects">Проекты</Nav.Link>
                </>
            )

        if (role === "EMPLOYEE")
            return (
                <>
                    <Nav.Link href="/account">Профиль</Nav.Link>
                    <Nav.Link href="/materials">Материалы</Nav.Link>
                    <Nav.Link href="/projects">Проекты</Nav.Link>
                </>
            )

        if (role === "USER")
            return (
                <>
                    <Nav.Link href="/account">Профиль</Nav.Link>
                    <Nav.Link href="/projects">Проекты</Nav.Link>
                </>
            )
        return (<></>);
    }

    return (
        <>
            <Navbar>
                <Navbar.Brand href="/">Trasco invest</Navbar.Brand>
                <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="nav-link">
                        <Nav.Link href="/">Главная</Nav.Link>
                        <Nav.Link href="/about">О нас</Nav.Link>
                        <Nav.Link href="/contact">Контакты</Nav.Link>
                        {renderRoleRoutes()}
                    </Nav>
                </Navbar.Collapse>
                <Row className="justify-content-end">
                    <Col>
                        {!!role? <Nav.Link className="nav-button me-3" onClick={onExit}>Выйти</Nav.Link>:
                        <Nav.Link className="nav-button me-3" onClick={onAuthorization}>Войти</Nav.Link>}
                    </Col>
                </Row>
            </Navbar>

            {showAuthorizationModal && (<AuthorizationModal setRole={setRole} onHide={() => setShowAuthorizationModal(false)}/>)}
        </>
    );
}

export default NavigationBar;
